tjs_uint16 TVPTranslateAndroidKeyToVirtualKey( tjs_int androidKey ) {
	if( androidKey <= AKEYCODE_HELP ) {
		return VK_TRANSLATE_TABLE[androidKey];
	}
	return 0;
}

